import React, {useState} from 'react';
import CanvasJSReact from './canvasjs.react';
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

const dictToList = (dict) => {
	let res = [];
	for (const key in dict){
		res.push({"x":key,
			"y":dict[key]
		});
	}
	return res;
}
const Graph = ()=>{
	const [data, setData] = useState({});
	const options = {
		animationEnabled: true,
		exportEnabled: true,
		theme: "light2", //"light1", "dark1", "dark2"
		title:{
			text: "Simple Column Chart with Index Labels"
		},
		axisY: {
			includeZero: true
		},
		data: [{
			type: "column", //change type to bar, line, area, pie, etc
			//indexLabel: "{y}", //Shows y value on all Data Points
			indexLabelFontColor: "#5A5757",
			indexLabelPlacement: "outside",
			dataPoints:dictToList(data)
		}]
	};
	function functionIntegral(x){
		return 1/2 * x**2;
	}
	const endBound = 1.414;
	function binarySearch(func, val, start, end){
		let left = start;
		let right = end;
		while (left < right){
			let center = left + (right - left)/2;
			if (center < val){
				left = center;
			}else if (center > val){
				right = center;
			}else{
				return center;
			}
		}
	}
	let results = data;
	function generate(){
		for (let i = 0; i < 10000; i++){
			const generatedNum = binarySearch(functionIntegral, Math.random(), 0, endBound);
			let roundedNum = generatedNum.toFixed(1);
			if (!(roundedNum in results)){
				results[roundedNum] = 0;
			}
			results[roundedNum] += 1;
		}
	}

	const delayRun = (delay)=>{
		setTimeout(()=>{setData(results)}, delay);
	}
	generate();
	delayRun(500);

	return (
	<div>
		<CanvasJSChart options = {options} 
		/>
	</div>
	);
}
export default Graph;
